import React from "react";
import "./response.css";

const Response = ({ text, isActive }) => {
  return (
    <div
      className="gpt3__header-content__input__response"
      style={{
        display: isActive ? "flex" : "none",
        transition: isActive ? "transform 5s" : "none",
        transform: "scale(4)",
      }}
    >
      <div className="gpt3__header-content__input__response--text">{text}</div>
    </div>
  );
};
export default Response;
