import React from "react";
import Typewriter from "../typewriter/Typewriter";
import "./greeting.css";

const Greeting = () => {
  const text = [
    "Explore new ideas for marketing campaigns and iterate through the best content, copy, and design rapidly with the help of SentenceSmith.",
    "Tell SentenceSmith what's on your mind and generate great insight into what you're thinking.",
    "Our interface can help generate ideas by providing structure for your brainstorming session.",
    "SentenceSmith is a multilingual tool, this includes the ability to create code from natural language.",
  ];
  return (
    <>
      <h1 className="gradient__text">
        SentenceSmith: Create amazing content with the power of AI!
      </h1>
      <Typewriter text={text} />
    </>
  );
};

export default Greeting;
