import React, { useEffect, useState, useRef } from "react";

const Typewriter = ({ text }) => {
  const index = useRef(0);
  const char = useRef(0);

  const [currentText, setCurrentText] = useState("");

  console.log(text[index.current].length);

  // Reset the index in text to loop through the next prompt
  useEffect(() => {
    index.current = 0;
    setCurrentText("");
    console.log("index set to 0");
  }, [text]);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (index.current < text.length) {
        if (char.current < text[index.current].length) {
          setCurrentText((value) => value + text[index.current][char.current]);
          char.current += 1;
        }
      }
    }, 100);
    return () => {
      clearTimeout(timeoutId);
      index.current += 1;
    };
  }, [currentText, index.current]);
  return <p>{currentText}</p>;
};
export default Typewriter;
